  .demo  .not .fancy {
      text-shadow: 2px 2px 3px gold;
    }
    /* 类名不是 `.fancy` 的 <p> 元素 */
   .demo  .not p:not(.fancy) {
      color: green;
    }
    /* 非 <p> 元素 */ 
  .demo   .not  :not(p) {
      text-decoration: underline;
    }
    /* 既不是 <div> 也不是 <span> 的元素 */
  .demo   .not  :not(div):not(span) {
      font-weight: bold;
    }
